var weapons = {};
weapons_raw.item_list.forEach(item => {
    weapons[item.item_id] = item;
});

function get_local_weapon (weapon_id) {
    if (weapons.hasOwnProperty(weapon_id)) {
        return weapons[weapon_id];
    }
    else {
        console.log('unknown weapon_id ',weapon_id);
        // todo check if looks legit and get_weapon again for future calls
        return false;
    }
}

function display_weapon_and_type(weapon_id) {
    weapon = get_local_weapon(weapon_id);
    if (weapon && weapon.hasOwnProperty('name')) {
        type = get_weapon_type (weapon.item_category_id);
    }
    else {
        weapon = {};
        weapon.name={};
        weapon.name.en = "[unknown]";
        type = '[unknown]';
    }
    return '<span class="weapon_name">'+weapon.name.en+'</span> <span class="weapon_type"> ('+type+')</span> ';
}

function get_weapon (weapon_id) {
    // check if weapon_id already in local cache
    // no local, so need to get then update
    /* if (1==0) {
        // have local
        return weapon;
    } */
    if (!weapon_id) {
        console.log('get_weapon called with:',weapon_id);
        return false;
    }
    if (window.weapons.hasOwnProperty(weapon_id)) {
        // have local
        //return dfd.resolve(window.characters[character_id]);
         return window.weapons[weapon_id];
    }
    else {
        if (weapon_id=="0") {
            return false;
        }
        // handle as promise
        console.log('no local weapon: ',weapon_id);
        var dfd = jQuery.Deferred();
        //https://census.daybreakgames.com/get/ps2:v2/item?c:lang=en&c:join=item_to_weapon(weapon)&c:limit=1&weapon_id=1
        var url = "https://census.daybreakgames.com/s:BAX/json/get/ps2:v2/item?c:lang=en&c:join=item_to_weapon(weapon)&c:limit=1&item_id=" + weapon_id + '&callback=?';
        //var url = "https://census.daybreakgames.com/s:BAX/json/get/ps2:v2/item?c:lang=en&c:join=item_category&c:join=item_to_weapon(weapon)&c:limit=1&item_id=" + weapon_id + '&callback=?';
        //console.log('Getting char info from census for ' + character_id);
        jQuery.getJSON(url,function(json){
            var weapon = json;
            console.log('got remote weapon:',weapon);
            console.log(weapon);
            window.weapons[weapon_id] = weapon.item_list[0];
            dfd.resolve(weapon);
        }).error(function() { data={}; data.payload={}; data.payload.timestamp = Math.floor(Date.now() / 1000); insert_row (data, 'Error getting weapon '+weapon_id+' from API'); });
        return dfd.promise();
    } 
}

function get_weapon_type (category_id) {
    if (item_categories.hasOwnProperty(category_id)) {
        return item_categories[category_id].name.en;
    }
    else {
        return "[unknown]";
    }
}

added_weapons = [];
custom_trigger_select_list = document.getElementById('custom_trigger_weapon_id');
for (const weapon in weapons) {
    option = document.createElement('option');
    option.value = weapons[weapon].item_id;
    if (weapons[weapon].hasOwnProperty('name')  && weapons[weapon].item_category_id!="141"  && weapons[weapon].item_category_id!="146" && weapons[weapon].item_category_id!="102") {
        // must have name, not be vehicle weapon, not tank, bounty, shield, and not a boosting item ;)
        option.text = weapons[weapon].name.en;
        option.dataset.item_category_id = weapons[weapon].item_category_id;
        if (added_weapons.includes(option.text)) {
            // skip, already in from another faction
        }
        else {
            added_weapons.push(option.text);
            custom_trigger_select_list.appendChild(option);
        }
    }
    else {
        //console.log('Item/weapon ',weapons[weapon],' has no name property');
    }
}

